    --notice-bg: @overlay0;
    --notice-color: @text;
    --menu-disabled-color: @crust;
    --control-bg: @accent-color;
    --a-visited: @accent-color;
    --nav-bar-border: none;
    --feed-alt-bg: @surface1;
    --nav-selected: @accent-color;

    .tab_options > ul > li.currentpage,
    .tab_options > ul > li.ui-state-active,
    .tab_options > ul > li:hover {
      border-bottom-color: @accent-color;
    }

    .site_btn_secondary {
      color: @subtext1;
      background: @surface1;
      &:hover {
        color: @accent-color;
        background: fade(@surface1, 80%);
      }
    }

    .site_btn,
    .site_btn_alt,
    #subscribe_full .site_btn,
    .pagination p a.pagination_page,
    .pagination p > span.pagination_page,
    .vert_menu > ul > li:only-child > a.current_select,
    .vert_menu > ul > li > a.current_select {
      color: @base;
      background: @accent-color;
      &:not(:disabled, [data-disabled="disabled"]):hover {
        background: fade(@accent-color, 80%);
      }
    }

    #profile-bar #profile-actions .togglable.selected {
      background: @accent-color;
      color: @base;
    }

    .tipso_bubble .bottom {
      background: @surface0 !important;
    }

    /* because the icons on the display mode buttons are white, we cant have white base here, so we improvise for latte */
    & when (@lookup = latte) {
      #display_modes a {
        background: @text !important;
        box-shadow: none !important;
      }

      /* fix nav menu dropdown icons */
      .navbar_icon .dropdown {
        --svozk: var(--s) -256px -326px !important;
        background: var(--s) -256px -326px !important;
      }

      .content-actions li {
        background: @surface0;

        & a,
        .link,
        .js_link,
        .js_link_m {
          color: @text;
        }
      }

      .content-actions li .js_link:hover,
      .content-actions li .js_link_m:hover,
      .content-actions li .link:hover,
      .content-actions li a:hover {
        & .material-icons {
          color: @text !important;
        }

        color: @text !important;
        background: @accent-color !important;
      }

      .content-actions li .material-icons {
        color: @subtext0 !important;
      }
    }

    & when not (@lookup = latte) {
      #display_modes a {
        background: @base !important;
        box-shadow: none !important;
      }
    }

    #display_modes a.selected,
    #display_modes a:hover {
      background: @accent-color;
    }

    .navigation-wrap .navmenu a {
      text-shadow: none !important;
    }

    #header
      #header-wrap
      .header_text
      > ul
      > li
      > .material-btn.mem_submit
      .material-icons,
    #header #header-wrap .header_text > ul > li > a.mem_submit .material-icons,
    #mobileRightPanel
      .sidr-inner
      > ul
      > li
      > .material-btn.mem_submit
      .material-icons,
    #mobileRightPanel .sidr-inner > ul > li > a.mem_submit .material-icons {
      color: @green !important;
    }

    #header
      #header-wrap
      .header_text
      > ul
      > li
      > .material-btn[data-alert="1"].mem_notifications
      .material-icons,
    #header
      #header-wrap
      .header_text
      > ul
      > li
      > .material-btn[data-alert="1"].mem_tickets
      .material-icons,
    #header
      #header-wrap
      .header_text
      > ul
      > li
      > a[data-alert="1"].mem_notifications
      .material-icons,
    #header
      #header-wrap
      .header_text
      > ul
      > li
      > a[data-alert="1"].mem_tickets
      .material-icons,
    #mobileRightPanel
      .sidr-inner
      > ul
      > li
      > .material-btn[data-alert="1"].mem_notifications
      .material-icons,
    #mobileRightPanel
      .sidr-inner
      > ul
      > li
      > .material-btn[data-alert="1"].mem_tickets
      .material-icons,
    #mobileRightPanel
      .sidr-inner
      > ul
      > li
      > a[data-alert="1"].mem_notifications
      .material-icons,
    #mobileRightPanel
      .sidr-inner
      > ul
      > li
      > a[data-alert="1"].mem_tickets
      .material-icons {
      color: @yellow;
    }

    .material-icons,
    .mem_submit > span,
    .mem_notifications > span {
      color: @text !important;
    }

    .green {
      background: @green;
      color: @base;

      & .material-icons {
        color: @base;
      }
    }

    .content-actions li:not(:only-child, :first-child) {
      border-top: none;
    }

    .tipso_content,
    .member_profile_card_right {
      background: @surface0;
    }

    .tipso_arrow {
      border-color: @surface0;
    }

    #popular-reel .caption {
      & when (@lookup = latte) {
        background: fade(@text, 50%) !important;

        .pop-title {
          color: @base !important;
          text-shadow: none;
        }

        .caption-subtitle {
          color: @crust !important;
          text-shadow: none;
        }
      }

      & when not (@lookup = latte) {
        background: fade(@base, 50%) !important;

        .pop-title {
          color: @text !important;
          text-shadow: none;
        }

        .caption-subtitle {
          color: @subtext0 !important;
          text-shadow: none;
        }

        .content-actions li {
          background: @surface0;

          & a,
          .link,
          .js_link,
          .js_link_m {
            color: @text;
          }
        }

        .content-actions li .js_link:hover,
        .content-actions li .js_link_m:hover,
        .content-actions li .link:hover,
        .content-actions li a:hover {
          & .material-icons {
            color: @accent-color !important;
          }
          color: @accent-color !important;
          background: @surface1 !important;
        }

        .content-actions li .material-icons {
          color: @subtext0 !important;
        }
      }
    }

    #content_notice .site_btn,
    #content_notice .site_btn_alt {
      text-shadow: none;
    }

    .custom_input:focus,
    input:focus,
    select:focus,
    textarea:focus {
      border-color: @accent-color;
    }
  }

  [data-theme="light"] {
    #catppuccin(@lightFlavor, @accentColor);
  }

  [data-theme="dark"] {
    #catppuccin(@darkFlavor, @accentColor);
  }
}

// vim:ft=less

/* ==UserStyle==
@name ChatGPT Catppuccin
@namespace github.com/catppuccin/userstyles/styles/chatgpt
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/chatgpt
@version 0.0.8
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/chatgpt/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Achatgpt
@description Soothing pastel theme for ChatGPT
@author Catppuccin
@license MIT

@preprocessor  less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green*", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */
@-moz-document domain("chat.openai.com") {
  @import url("https://unpkg.com/@catppuccin/highlightjs@0.1.2/css/catppuccin.variables.css");

  .light body,
  .light html {
    #catppuccin(@lightFlavor, @accentColor);
  }

  :root,
  .dark body,
  .dark html {
    #catppuccin(@darkFlavor, @accentColor);
  }

  /* prettier-ignore */
  @catppuccin: {
    @latte:     { @rosewater: #dc8a78; @flamingo: #dd7878; @pink: #ea76cb; @mauve: #8839ef; @red: #d20f39; @maroon: #e64553; @peach: #fe640b; @yellow: #df8e1d; @green: #40a02b; @teal: #179299; @sky: #04a5e5; @sapphire: #209fb5; @blue: #1e66f5; @lavender: #7287fd; @text: #4c4f69; @subtext1: #5c5f77; @subtext0: #6c6f85; @overlay2: #7c7f93; @overlay1: #8c8fa1; @overlay0: #9ca0b0; @surface2: #acb0be; @surface1: #bcc0cc; @surface0: #ccd0da; @base: #eff1f5; @mantle: #e6e9ef; @crust: #dce0e8; };
    @frappe:    { @rosewater: #f2d5cf; @flamingo: #eebebe; @pink: #f4b8e4; @mauve: #ca9ee6; @red: #e78284; @maroon: #ea999c; @peach: #ef9f76; @yellow: #e5c890; @green: #a6d189; @teal: #81c8be; @sky: #99d1db; @sapphire: #85c1dc; @blue: #8caaee; @lavender: #babbf1; @text: #c6d0f5; @subtext1: #b5bfe2; @subtext0: #a5adce; @overlay2: #949cbb; @overlay1: #838ba7; @overlay0: #737994; @surface2: #626880; @surface1: #51576d; @surface0: #414559; @base: #303446; @mantle: #292c3c; @crust: #232634; };
    @macchiato: { @rosewater: #f4dbd6; @flamingo: #f0c6c6; @pink: #f5bde6; @mauve: #c6a0f6; @red: #ed8796; @maroon: #ee99a0; @peach: #f5a97f; @yellow: #eed49f; @green: #a6da95; @teal: #8bd5ca; @sky: #91d7e3; @sapphire: #7dc4e4; @blue: #8aadf4; @lavender: #b7bdf8; @text: #cad3f5; @subtext1: #b8c0e0; @subtext0: #a5adcb; @overlay2: #939ab7; @overlay1: #8087a2; @overlay0: #6e738d; @surface2: #5b6078; @surface1: #494d64; @surface0: #363a4f; @base: #24273a; @mantle: #1e2030; @crust: #181926; };
    @mocha:     { @rosewater: #f5e0dc; @flamingo: #f2cdcd; @pink: #f5c2e7; @mauve: #cba6f7; @red: #f38ba8; @maroon: #eba0ac; @peach: #fab387; @yellow: #f9e2af; @green: #a6e3a1; @teal: #94e2d5; @sky: #89dceb; @sapphire: #74c7ec; @blue: #89b4fa; @lavender: #b4befe; @text: #cdd6f4; @subtext1: #bac2de; @subtext0: #a6adc8; @overlay2: #9399b2; @overlay1: #7f849c; @overlay0: #6c7086; @surface2: #585b70; @surface1: #45475a; @surface0: #313244; @base: #1e1e2e; @mantle: #181825; @crust: #11111b; };
  }

  @color: white;
  #rgbify(@color) {
    @rgb-raw: red(@color), green(@color), blue(@color);
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    --ctp-rosewater: #rgbify(@rosewater) [];
    --ctp-flamingo: #rgbify(@flamingo) [];
    --ctp-pink: #rgbify(@pink) [];
    --ctp-mauve: #rgbify(@mauve) [];
    --ctp-red: #rgbify(@red) [];
    --ctp-maroon: #rgbify(@maroon) [];
    --ctp-peach: #rgbify(@peach) [];
    --ctp-yellow: #rgbify(@yellow) [];
    --ctp-green: #rgbify(@green) [];
    --ctp-teal: #rgbify(@teal) [];
    --ctp-sky: #rgbify(@sky) [];
    --ctp-sapphire: #rgbify(@sapphire) [];
    --ctp-blue: #rgbify(@blue) [];
    --ctp-lavender: #rgbify(@lavender) [];
    --ctp-text: #rgbify(@text) [];
    --ctp-subtext1: #rgbify(@subtext1) [];
    --ctp-subtext0: #rgbify(@subtext0) [];
    --ctp-overlay2: #rgbify(@overlay2) [];
    --ctp-overlay1: #rgbify(@overlay1) [];
    --ctp-overlay0: #rgbify(@overlay0) [];
    --ctp-surface2: #rgbify(@surface2) [];
    --ctp-surface1: #rgbify(@surface1) [];
    --ctp-surface0: #rgbify(@surface0) [];
    --ctp-base: #rgbify(@crust) [];
    --ctp-mantle: #rgbify(@mantle) [];
    --ctp-crust: #rgbify(@crust) [];

    --text-primary: @text;
    --text-secondary: @subtext0;
    --text-tertiary: @subtext1;
    --surface-primary: @mantle;
    --surface-secondary: @base;
    --surface-tertiary: @surface0;
    --border-light: fade(@overlay2, 10%);
    --border-medium: fade(@overlay2, 15%);
    --border-heavy: fade(@overlay2, 20%);
    --border-xheavy: fade(@overlay2, 25%);

    background: @base !important;
    color: @text;

    /* Main page */
    div[class*="bg-gray-"]:not(
        [class*="bg-gray-900"],
        [class*="bg-gray-700"],
        .bg-black .bg-gray-800,
        [class*="bg-gray-600/70"], /* Modal background */
        [id^="headlessui-menu-items-"], /* Help menu */
        [class*=":radix-highlighted:hover:bg-[#2E2F33]"] /* Settings > Theme selector drop down */
      ),
    div[class*="bg-vert-dark-gradient"] {
      background: @base !important;
    }

    /* ToS violating message. Don't ask how this was tested. */
    .text-orange-500 {
      color: @peach;

      .bg-orange-500\/10.border-orange-500 {
        background-color: fade(@peach, 10%);
        border-color: @peach;
      }
    }

    /* Chatbox */
    [class*=":shadow-[0_0_0_2px_"] {
      --tw-shadow: 0 0 0 2px @overlay0 !important;
      ::placeholder {
        color: @overlay0 !important;
      }
    }

    /* Go to end arrow */
    [class*=":bg-white/10"] {
      background-color: @surface0;
      border-color: @overlay0;
    }

    /* Message suggestions */
    [class*=":from-gray-700"] {
      --tw-gradient-from: if(@lookup = latte, @crust, @surface2)
        var(--tw-gradient-from-position);
      --tw-gradient-to: transparent var(--tw-gradient-to-position);
    }

    /* Help Menu */
    [id^="headlessui-menu-items-"] {
      background-color: @mantle !important;
      [id^="headlessui-menu-item-"]:hover {
        background-color: @surface0 !important;
      }
    }

    /* Modal Background */
    [class*="bg-gray-600/70"] {
      background: fade(@base, 70%) !important;
    }

    /* Sidebar */
    [class*="bg-gray-900"]:not(.\!bg-transparent) {
      /* Also, Shared Links */
      background-color: @mantle !important;
    }

    div {
      --text-primary: @text;
      --text-secondary: @subtext0;
      --text-tertiary: @subtext1;
      --surface-primary: @surface0;
      --surface-secondary: @surface1;
      --surface-tertiary: @surface2;
      --border-light: fade(@overlay2, 10%);
      --border-medium: fade(@overlay2, 15%);
      --border-heavy: fade(@overlay2, 20%);
      --border-xheavy: fade(@overlay2, 25%);
    }

    nav {
      .sticky.bg-black, /* New Chat */
      h3[class*=":bg-black"] /* Today, Yesterday */ {
        background-color: transparent !important;
      }

      /* TODO: is it used? */
      .bg-gray-900 .bg-gray-800 {
        background-color: @surface0 !important;
      }

      /* Conversation */
      .group {
        /* Rename */
        a[href^="/c/"] ~ .absolute {
          input {
            border-color: @accent-color;
            --tw-ring-color: @accent-color;
          }
        }
        /* Gradient */
        a[href^="/c/"] .bg-gradient-to-l {
          --tw-gradient-from: @crust var(--tw-gradient-from-position);
        }
        &:hover a[href^="/c/"] .bg-gradient-to-l {
          --tw-gradient-from: var(--surface-primary)
            var(--tw-gradient-from-position);
        }
      }

      /* TODO: is it used? */
      /* Upgrade Plan */
      .shim-yellow {
        display: none;
      }
    }

    [class*=":hover:bg-white/5"][class*="hover:bg-black/5"] {
      /* ... Menu */
      background-color: var(--surface-primary) !important;
      &:hover {
        background-color: var(--surface-tertiary) !important;
      }
    }

    /* Search */
    div[class*="bg-gray-700"] {
      background-color: if(@lookup = latte, @base, @surface0) !important;
    }

    [class*="text-gray-"]:not(.text-gray-500),
    [class*="text-white"]:not(.btn-primary .text-white, .bg-green-600) {
      color: @text !important;
    }

    .text-gray-500 {
      color: @subtext0;
    }

    div[class*="border-gray"],
    div[class*="border-white"] {
      border-color: @overlay0 !important;
    }

    input,
    textarea {
      color: @text;
      &::placeholder {
        color: @overlay0;
      }
    }

    [class*="bg-white/5"] {
      background-color: @surface0 !important;
    }

    /* Bot messages */
    .prose {
      --tw-prose-body: @text !important;
      --tw-prose-bold: @text !important;
      --tw-prose-code: @text !important;
      --tw-prose-links: @text !important;
      --tw-prose-headings: @overlay1 !important;
    }

    .bg-black,
    [class*=":bg-black"] {
      background-color: @crust !important;
    }

    .bg-black .bg-gray-800 {
      background-color: @surface0 !important;
    }

    code.hljs,
    code[class*="language-"],
    pre[class*="language-"] {
      color: @text !important;
    }

    .markdown {
      [type="checkbox"]:checked {
        background-color: @accent-color;
        @svg: escape(
          '<svg viewBox="0 0 16 16" fill="@{base}" xmlns="http://www.w3.org/2000/svg"><path d="M12.207 4.793a1 1 0 0 1 0 1.414l-5 5a1 1 0 0 1-1.414 0l-2-2a1 1 0 0 1 1.414-1.414L6.5 9.086l4.293-4.293a1 1 0 0 1 1.414 0z"/></svg>'
        );
        background-image: ~"url('data:image/svg+xml;charset=utf-8,@{svg}')";
      }

      a {
        color: @accent-color;
        &:hover {
          color: lighten(@accent-color, 5%);
          text-decoration-line: underline;
        }
      }

      th {
        background: @mantle;
        color: @text;
      }
    }

    /* Buttons */
    .btn-neutral,
    .btn-dark {
      background-color: if(@lookup = latte, @base, @surface1) !important;
      color: @text !important;
      border-color: @overlay0;
      &:hover {
        background-color: if(@lookup = latte, @crust, @surface2) !important;
      }
    }

    .btn-primary {
      background-color: @accent-color !important;
      color: @crust !important;
    }

    .btn-primary .text-white {
      color: @crust !important;
    }

    .btn-danger {
      background-color: @red !important;
      color: @crust !important;
    }

    .btn-disabled,
    .btn-disabled:hover {
      background-color: @surface0;
      color: @text;
    }

    /* TODO: is it used? */
    .text-2xl {
      color: @text !important;
    }

    /* TODO: is it used? */
    .dark [class*="bg-yellow-"] {
      color: @crust !important;
    }

    /* TODO: is it used? */
    .light [class*="bg-yellow-"] {
      color: @text !important;
    }

    /* TODO: is it used? */
    [class*="text-green"] {
      color: @accent-color !important;
    }

    .dark .dark\:hover\:bg-\[\#2A2B32\]:hover {
      background-color: @surface0;
    }

    [class*=":bg-gray-800"]:not(
        [class*="hover:bg-gray-800"],
        [class*="group-ui-open:bg-gray-800"]
      ) {
      background-color: @surface0;
    }

    [class*=":hover:bg-gray-800"]:hover {
      background-color: @surface1;
    }

    .radix-state-active\:bg-gray-800[data-state="active"] {
      background-color: @surface0;
    }

    /* Settings > Theme selector drop down */
    [class*=":hover:bg-[#494A54]"].bg-white {
      background-color: @mantle;
      &:hover {
        background-color: @surface0;
      }
    }

    [class*=":focus-visible:border-green-500"]:focus-visible {
      border-color: @accent-color;
    }

    [class*=":radix-highlighted:hover:bg-[#2E2F33]"]:hover {
      background-color: @surface0 !important;
    }

    /* Shared Links */
    a[href^="/share/"][class*="text-blue-400"] {
      color: @accent-color;
    }

    [class*=":border-white/10"] {
      border-color: fade(@overlay0, 10%);
    }

    .text-red-500 {
      color: @red;
    }

    /* Custom Instructions */
    .border-gray-100 {
      border-color: @overlay0;
    }

    .focus\:border-brand-green:focus {
      border-color: @accent-color;
    }

    /* Toggle button */
    .radix-state-checked\:bg-green-600 {
      &[data-state="checked"] {
        background-color: @accent-color;
        & > span {
          background-color: @surface0;
        }
      }
      &[data-state="unchecked"] {
        background-color: @surface0;
        & > span {
          background-color: @text;
        }
      }
    }

    /* ChatGPT Icon
      (Needs `layout.css.has-selector.enabled`. If it's disabled, no changes are made)
    */
    div:has(> [role="img"]) {
      background-color: @accent-color !important;
      & > [role="img"] {
        color: @base;
      }
    }

    /* SVG chat avatars (ChatGPT, and Anonymous)
      (Needs `layout.css.has-selector.enabled`. If it's disabled, no changes are made)
    */
    div:has(> svg.icon-sm:only-child) {
      background-color: @accent-color !important;
      & > svg {
        color: @base;
      }
    }

    /* Send button */
    [data-testid="send-button"][class*="bg-gray-900"] {
      background-color: @base !important;
      border-color: @overlay0;
      svg[class*="text-white"] {
        color: @accent-color !important;
      }
    }

    /* Notification Popup (Copied to clipboard) */
    .bg-green-600.text-white.border-green-600 {
      background-color: @accent-color;
      color: @base !important;
      border-color: @overlay0;
    }

    /* Share page > "Upgrade to Plus" button */
    a[href="/gpts/discovery"].bg-green-600.text-white {
      background-color: @accent-color;
      color: @base !important;
    }

    /* Login Page > buttons */
    .bg-\[\#3C46FF\] {
      background-color: @accent-color;
      color: @base;
    }

    .hover\:bg-\[\#0000FF\]:hover {
      background-color: lighten(@accent-color, 5%);
    }
  }
}
